void Init_Graphics(void){
	ST7735_InitR(INITR_REDTAB);
}

// x and y intentionally switched to cope with ST7735 coordinates
void Draw_Ship(Ship * ship) {
	const unsigned short * to_draw;
	switch (ship->type) {
		case MY_SHIP: to_draw = my_ship_bmp; break;
		case ENEMY1: to_draw = enemy1_bmp; break;
		case ENEMY2: to_draw = enemy2_bmp; break;
		case ENEMY3: to_draw = enemy3_bmp; break;
		case BOSS1: to_draw = boss1_bmp; break;
		case BOSS2: to_draw = boss2_bmp; break;		
	}
	ST7735_DrawBitmap(ship->y, ship->x, to_draw, ship->height, ship->width);
}

void Draw_Bullet(Bullet * bullet) {
	if (!bullet->valid) return;
	else {
		ST7735_DrawFastHLine(bullet->y, bullet->x, bullet->width, ST7735_WHITE);
		ST7735_DrawFastHLine((bullet->y)-1, bullet->x, bullet->width, ST7735_WHITE);
	}
}

void Draw_Explosion(Explosion * explosion){
	const unsigned short * to_draw;
	switch(explosion->stage){
		case 1:	to_draw = explosion1_bmp; break;
		case 2: to_draw = explosion2_bmp; break;
		case 3: to_draw = explosion3_bmp; break;
	}
	ST7735_DrawBitmap(explosion->x, explosion->y, to_draw, EXPLOSION_HEIGHT, EXPLOSION_WIDTH);	
}

void Draw_Enemies(void){
	Ship ** enemies = Get_Enemies();
	for (int i = 0; i < NUM_MAX_ENEMIES_COLUMN; i ++) {
		for (int j = 0; j < NUM_MAX_ENEMIES_ROW; j ++) {
			Ship * enemy = enemies[i * NUM_MAX_ENEMIES_COLUMN + j];
			if (enemy->valid) {
				ST7735_FillRect(enemy->yold, enemy->xold, enemy->height, enemy->width, ST7735_BLACK);
				Draw_Ship(enemies[i * NUM_MAX_ENEMIES_COLUMN + j]);
			}
			else if (enemy->valid == 0) {
				ST7735_FillRect(enemy->y, enemy->x, enemy->height, enemy->width, ST7735_BLACK);
				enemy->valid = -1;
			}
		}
	}
}

void Draw_Boss(void) {
	Ship * boss = Get_Boss();
	if (boss->valid) {
		Draw_Ship(boss);
	}
}
void Draw_My_Ship(void) {
	Ship * my_ship = Get_My_Ship();
	Draw_Ship(my_ship);
}

void Draw_Bullets(void) {
	Bullet ** my_bullets = Get_My_Bullets();
	for (int i = 0; i < NUM_MY_BULLETS; i ++) {
		Bullet * bullet = my_bullets[i];
		if (bullet->valid == 1){
			ST7735_FillRect(bullet->yold, bullet->xold, bullet->height, bullet->width, ST7735_BLACK);
			Draw_Bullet(bullet);
		}
		else if (bullet->valid == 0) {
			ST7735_FillRect(bullet->y, bullet->x, bullet->height, bullet->width, ST7735_BLACK);
			bullet->valid = -1;
		}
	}
	
	Bullet ** enemy_bullets = Get_Enemy_Bullets();
	for (int i = 0; i < NUM_ENEMY_BULLETS; i ++) {
		Bullet * bullet = enemy_bullets[i];
		if (bullet->valid == 1){
			ST7735_FillRect(bullet->yold, bullet->xold, bullet->height, bullet->width, ST7735_BLACK);
			Draw_Bullet(bullet);
		}
		else if (bullet->valid == 0) {
			ST7735_FillRect(bullet->y, bullet->x, bullet->height, bullet->width, ST7735_BLACK);
			bullet->valid = -1;
		}
	}
}
